import React, { useState, useEffect } from "react";
import { helpers } from "../../utils";
import styles from "./wine.module.css";

import axios from "axios";
const WineStats = () => {
  const [data, setData] = useState([]);

  // Define the grouping function
  function grouping(result, current) {
    debugger;
    const categary = current.Alcohol;
    if (!(categary in result)) {
      result[categary] = [];
    }
    result[categary].push({
      ...current,
      gamma: helpers.calculateGamma(current),
    });
    return result;
  }

  const results = {};

  async function fetchData() {
    debugger;
    try {
      const response = await axios.get("/data.json"); // Assuming the file is in the public folder

      console.log(response);
      // setData(response.data.data);
      let roleObj = {};
      const data = response.data.data.reduce(grouping, {});
      // setData(data);

      console.log(data);
      for (const key in data) {
        results[key] = {
          mean: helpers.calculateMean(data[key]),
          median: helpers.calculateMedian(data[key]),
          mode: helpers.calculateMode(data[key]),
        };
      }

      console.log(results);
      console.log(Object.keys(results));
      console.log(Object.values(results));
      setData(results);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <table>
        <tr>
          <th>Measure</th>

          {Object.keys(data).map((ele, index) => {
            return <th key={index}>{`Class ${ele}`}</th>;
          })}
        </tr>
        {Object.entries(data).map(([key, ele], ind) => {
          return (
            <tr>
              <td>{`Flavanoids ${key}`}</td>
              <td>{ele.mean}</td>
              <td>{ele.median}</td>
              <td>{ele.mode[0]}</td>
            </tr>
          );
        })}
      </table>
    </div>
  );
};

export default WineStats;
